    @Test
    public void test0() {
        int dist = solution.shortestBridge(new int[][]{{0, 1}, {1, 0}});
        assertThat(dist).isEqualTo(1);
    }

    @Test
    public void test2() {
        int dist = solution.shortestBridge(new int[][]{
                {0, 1, 0},
                {0, 0, 0},
                {0, 0, 1}});
        assertThat(dist).isEqualTo(2);
    }

